import { NextRequest, NextResponse } from "next/server";
import Groq from "groq-sdk";

const groq = new Groq({
  apiKey: process.env.GROQ_API_KEY,
});

export async function POST(request: NextRequest) {
  try {
    const { task, code, language, problem, hints, solution, size, context } = await request.json();

    let prompt = "";
    let model = "llama-3.3-70b-versatile";

    switch (task) {
      case "code_explain":
        prompt = `Explain this ${language} code in simple terms:\n\n${code}`;
        break;

      case "code_debug":
        prompt = `Debug this ${language} code and explain any issues:\n\n${code}`;
        break;

      case "code_refactor":
        prompt = `Refactor this ${language} code to make it more efficient and readable:\n\n${code}\n\nProvide the refactored code and explain the improvements.`;
        break;

      case "practice_hint":
        if (code && context) {
          prompt = `Give a helpful hint for completing this coding project. Don't reveal the full solution, just guide the user:\n\n${context}\n\nCurrent code:\n${code}`;
        } else if (size === "small") {
          prompt = `Give a small hint for this coding problem (don't reveal the solution):\n\n${problem}\n\nAvailable hints: ${hints}`;
        } else {
          prompt = `Give a bigger hint for this coding problem (still don't reveal the full solution):\n\n${problem}\n\nAvailable hints: ${hints}`;
        }
        break;

      case "practice_solution":
        prompt = `Explain the solution to this coding problem:\n\n${problem}\n\nSolution: ${solution}`;
        break;

      default:
        return NextResponse.json({ error: "Invalid task" }, { status: 400 });
    }

    const chatCompletion = await groq.chat.completions.create({
      messages: [
        {
          role: "user",
          content: prompt,
        },
      ],
      model: model,
      temperature: 0.7,
      max_tokens: 1024,
    });

    const response = chatCompletion.choices[0]?.message?.content || "";

    let extractedCode = "";
    if (task === "code_refactor") {
      const codeMatch = response.match(/```(?:javascript|python)?\n([\s\S]*?)\n```/);
      if (codeMatch) {
        extractedCode = codeMatch[1];
      }
    }

    return NextResponse.json({ 
      response,
      code: extractedCode,
    });
  } catch (error: any) {
    console.error("Groq API error:", error);
    return NextResponse.json(
      { error: error.message || "AI request failed" },
      { status: 500 }
    );
  }
}

app/editor/page.tsx.content:
"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { Navbar } from "@/components/Navbar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Lightbulb, Bug, Sparkles, Play, Loader2, Copy, Check } from "lucide-react";
import dynamic from "next/dynamic";

const MonacoEditor = dynamic(() => import("@monaco-editor/react"), { ssr: false });

export default function EditorPage() {
  const router = useRouter();
  const [user, setUser] = useState<any>(null);
  const [code, setCode] = useState("// Welcome to TechVerse Hub Editor!\n// Start coding here...\n\nfunction hello() {\n  console.log('Hello, World!');\n}\n\nhello();");
  const [language, setLanguage] = useState("javascript");
  const [aiResponse, setAiResponse] = useState("");
  const [aiLoading, setAiLoading] = useState(false);
  const [activeAction, setActiveAction] = useState<string | null>(null);
  const [output, setOutput] = useState("");
  const [running, setRunning] = useState(false);
  const [copied, setCopied] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const userStr = localStorage.getItem("user");
    if (!userStr) {
      router.push("/auth/login");
      return;
    }
    setUser(JSON.parse(userStr));
    setLoading(false);
  }, [router]);

  const handleAiAction = async (task: "code_explain" | "code_debug" | "code_refactor") => {
    setAiLoading(true);
    setAiResponse("");
    setActiveAction(task);

    try {
      const response = await fetch("/api/ai/groq", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          task,
          code,
          language,
        }),
      });

      const result = await response.json();
      setAiResponse(result.response || "No response received.");

      if (task === "code_refactor" && result.code) {
        const shouldReplace = confirm("Would you like to replace your code with the refactored version?");
        if (shouldReplace) {
          setCode(result.code);
        }
      }
    } catch (error) {
      setAiResponse("Error getting AI response. Please try again.");
    } finally {
      setAiLoading(false);
    }
  };

  const handleRunCode = async () => {
    setRunning(true);
    setOutput("");

    try {
      const response = await fetch("/api/run", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          code,
          language,
        }),
      });

      const result = await response.json();
      setOutput(result.output || result.error || "No output");
    } catch (error) {
      setOutput("Error running code");
    } finally {
      setRunning(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(code);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  const getActionLabel = (action: string | null) => {
    switch (action) {
      case "code_explain": return "Explanation";
      case "code_debug": return "Debug Analysis";
      case "code_refactor": return "Refactored Code";
      default: return "AI Assistant";
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-background">
        <Navbar />
        <div className="flex items-center justify-center min-h-[60vh]">
          <div className="text-center">
            <Loader2 className="h-12 w-12 animate-spin text-primary mx-auto mb-4" />
            <p className="text-muted-foreground">Loading editor...</p>
          </div>
        </div>
      </div>
    );
  }

  if (!user) return null;

  return (
    <div className="min-h-screen bg-background">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <div className="mb-6">
          <h1 className="text-3xl font-bold mb-2">Code Editor</h1>
          <p className="text-muted-foreground">Write code with AI-powered assistance</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          <div className="lg:col-span-2 space-y-4">
            <Card>
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <CardTitle>Editor</CardTitle>
                  <div className="flex items-center gap-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={copyToClipboard}
                      className="h-8"
                    >
                      {copied ? (
                        <Check className="h-4 w-4 text-green-500" />
                      ) : (
                        <Copy className="h-4 w-4" />
                      )}
                    </Button>
                    <div className="flex gap-1">
                      {["javascript", "python"].map((lang) => (
                        <Badge
                          key={lang}
                          variant={language === lang ? "default" : "outline"}
                          className="cursor-pointer capitalize"
                          onClick={() => setLanguage(lang)}
                        >
                          {lang}
                        </Badge>
                      ))}
                    </div>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="border rounded-lg overflow-hidden">
                  <MonacoEditor
                    height="450px"
                    language={language}
                    value={code}
                    onChange={(value) => setCode(value || "")}
                    theme="vs-dark"
                    options={{
                      minimap: { enabled: false },
                      fontSize: 14,
                      padding: { top: 16, bottom: 16 },
                      scrollBeyondLastLine: false,
                      wordWrap: "on",
                    }}
                  />
                </div>

                <div className="flex flex-wrap gap-2">
                  <Button
                    onClick={() => handleAiAction("code_explain")}
                    disabled={aiLoading}
                    variant="outline"
                    size="sm"
                    className="gap-2"
                  >
                    <Lightbulb className="h-4 w-4" />
                    Explain
                  </Button>
                  <Button
                    onClick={() => handleAiAction("code_debug")}
                    disabled={aiLoading}
                    variant="outline"
                    size="sm"
                    className="gap-2"
                  >
                    <Bug className="h-4 w-4" />
                    Debug
                  </Button>
                  <Button
                    onClick={() => handleAiAction("code_refactor")}
                    disabled={aiLoading}
                    variant="outline"
                    size="sm"
                    className="gap-2"
                  >
                    <Sparkles className="h-4 w-4" />
                    Refactor
                  </Button>
                  <div className="flex-1" />
                  <Button onClick={handleRunCode} disabled={running} size="sm" className="gap-2">
                    {running ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <Play className="h-4 w-4" />
                    )}
                    Run Code
                  </Button>
                </div>

                {output && (
                  <div className="bg-secondary/50 rounded-lg p-4">
                    <p className="text-sm font-medium mb-2">Output:</p>
                    <pre className="text-sm whitespace-pre-wrap font-mono">{output}</pre>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>

          <div className="lg:col-span-1">
            <Card className="sticky top-24">
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Sparkles className="h-5 w-5 text-primary" />
                  {getActionLabel(activeAction)}
                </CardTitle>
              </CardHeader>
              <CardContent>
                {aiLoading && (
                  <div className="flex flex-col items-center justify-center py-12 text-muted-foreground">
                    <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin mb-4" />
                    <p className="text-sm">AI is analyzing your code...</p>
                  </div>
                )}
                {!aiLoading && !aiResponse && (
                  <div className="text-center py-12 text-muted-foreground">
                    <Sparkles className="h-12 w-12 mx-auto mb-4 opacity-20" />
                    <p className="text-sm">Click an AI action button to get help with your code</p>
                  </div>
                )}
                {!aiLoading && aiResponse && (
                  <div className="prose dark:prose-invert max-w-none">
                    <p className="whitespace-pre-wrap text-sm leading-relaxed">{aiResponse}</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

app/auth/signup/page.tsx.content:
"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { supabase, isSupabaseConfigured } from "@/lib/supabase";
import { Code2, ArrowLeft, Loader2 } from "lucide-react";

export default function SignupPage() {
  const router = useRouter();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSignup = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    if (!isSupabaseConfigured()) {
      setError("Supabase is not configured. Please set up environment variables.");
      setLoading(false);
      return;
    }

    if (password !== confirmPassword) {
      setError("Passwords do not match");
      setLoading(false);
      return;
    }

    if (password.length < 6) {
      setError("Password must be at least 6 characters");
      setLoading(false);
      return;
    }

    try {
      const { data, error } = await supabase.auth.signUp({
        email,
        password,
      });

      if (error) {
        if (error.message.toLowerCase().includes("already registered") || 
            error.message.toLowerCase().includes("already exists") ||
            error.message.toLowerCase().includes("user already")) {
          setError("An account with this email already exists. Please sign in instead.");
        } else {
          throw error;
        }
        setLoading(false);
        return;
      }

      if (data.user && data.user.identities && data.user.identities.length === 0) {
        setError("An account with this email already exists. Please sign in instead.");
        setLoading(false);
        return;
      }

      if (data.user) {
        try {
          await fetch("/api/email/welcome", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ email }),
          });
        } catch {}

        localStorage.setItem("user", JSON.stringify({ id: data.user.id, email: data.user.email }));
        router.push("/dashboard");
      }
    } catch (err: any) {
      if (err.message.toLowerCase().includes("already")) {
        setError("An account with this email already exists. Please sign in instead.");
      } else {
        setError(err.message || "Signup failed. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col bg-background">
      <nav className="border-b">
        <div className="container mx-auto px-4 py-4">
          <Link href="/" className="flex items-center gap-2 w-fit">
            <ArrowLeft className="h-4 w-4" />
            <Code2 className="h-6 w-6 text-primary" />
            <span className="font-bold">TechVerse Hub</span>
          </Link>
        </div>
      </nav>

      <div className="flex-1 flex items-center justify-center p-4">
        <div className="w-full max-w-md">
          <Card className="border-border/50 shadow-xl">
            <CardHeader className="text-center">
              <div className="mx-auto w-12 h-12 rounded-xl bg-primary/10 flex items-center justify-center mb-4">
                <Code2 className="h-6 w-6 text-primary" />
              </div>
              <CardTitle className="text-2xl">Create your account</CardTitle>
              <CardDescription>Start your coding journey today</CardDescription>
            </CardHeader>
            <form onSubmit={handleSignup}>
              <CardContent className="space-y-4">
                {error && (
                  <div className="bg-destructive/10 border border-destructive/30 text-destructive text-sm px-4 py-3 rounded-lg">
                    {error}
                  </div>
                )}
                <div className="space-y-2">
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    placeholder="you@example.com"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    className="h-11"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="password">Password</Label>
                  <Input
                    id="password"
                    type="password"
                    placeholder="At least 6 characters"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    className="h-11"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="confirmPassword">Confirm Password</Label>
                  <Input
                    id="confirmPassword"
                    type="password"
                    placeholder="Confirm your password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    required
                    className="h-11"
                  />
                </div>
              </CardContent>
              <CardFooter className="flex flex-col gap-4">
                <Button type="submit" className="w-full h-11" disabled={loading}>
                  {loading ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Creating account...
                    </>
                  ) : (
                    "Create account"
                  )}
                </Button>
                <p className="text-sm text-center text-muted-foreground">
                  Already have an account?{" "}
                  <Link href="/auth/login" className="text-primary hover:underline font-medium">
                    Sign in
                  </Link>
                </p>
              </CardFooter>
            </form>
          </Card>
        </div>
      </div>
    </div>
  );
}

app/api/ai/gemini/route.ts.content:
import { NextRequest, NextResponse } from "next/server";
import { GoogleGenerativeAI } from "@google/generative-ai";

const genAI = new GoogleGenerativeAI(process.env.GEMINI_API_KEY || "");

export async function POST(request: NextRequest) {
  try {
    const { task, content, topic } = await request.json();

    let prompt = "";

    switch (task) {
      case "lesson_explain":
        prompt = `Explain this lesson in simple terms for beginners:\n\n${content}`;
        break;

      case "simplify_concept":
        prompt = `Simplify this programming concept for a beginner:\n\n${topic}`;
        break;

      case "generate_quiz":
        prompt = `Generate 3 quiz questions about this topic:\n\n${content}`;
        break;

      case "study_helper":
        prompt = `Help me understand this programming concept:\n\n${topic}`;
        break;

      default:
        return NextResponse.json({ error: "Invalid task" }, { status: 400 });
    }

    const model = genAI.getGenerativeModel({ model: "gemini-2.0-flash" });
    const result = await model.generateContent(prompt);
    const response = result.response.text();

    return NextResponse.json({ response });
  } catch (error: any) {
    console.error("Gemini API error:", error);
    return NextResponse.json(
      { error: error.message || "AI request failed" },
      { status: 500 }
    );
  }
}

app/api/email/reset/route.ts.content:
import { NextRequest, NextResponse } from "next/server";
import nodemailer from "nodemailer";

export async function POST(request: NextRequest) {
  try {
    const { email } = await request.json();

    if (!process.env.SMTP_HOST || !process.env.SMTP_USER || !process.env.SMTP_PASS) {
      console.log("SMTP not configured, skipping reset email");
      return NextResponse.json({ success: true, skipped: true });
    }

    const transporter = nodemailer.createTransport({
      host: process.env.SMTP_HOST,
      port: parseInt(process.env.SMTP_PORT || "587"),
      secure: process.env.SMTP_PORT === "465",
      auth: {
        user: process.env.SMTP_USER,
        pass: process.env.SMTP_PASS,
      },
    });

    const userName = email.split('@')[0];
    const siteUrl = process.env.REPLIT_DEV_DOMAIN 
      ? `https://${process.env.REPLIT_DEV_DOMAIN}` 
      : 'https://techverse-hub.replit.app';

    const mailOptions = {
      from: `"TechVerse Hub" <${process.env.SMTP_USER}>`,
      to: email,
      subject: "Reset Your TechVerse Hub Password",
      html: `
        <div style="font-family: 'Segoe UI', Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px; background-color: #f8fafc;">
          <div style="background: linear-gradient(135deg, #ef4444 0%, #dc2626 100%); padding: 40px 20px; text-align: center; border-radius: 12px 12px 0 0;">
            <h1 style="color: white; margin: 0; font-size: 28px;">Password Reset Request</h1>
          </div>
          <div style="background: white; padding: 30px; border-radius: 0 0 12px 12px; box-shadow: 0 4px 6px rgba(0,0,0,0.1);">
            <p style="font-size: 18px; color: #1e293b;">Hi ${userName},</p>
            <p style="font-size: 16px; color: #475569; line-height: 1.6;">We received a request to reset your password for your TechVerse Hub account.</p>
            <div style="background: #fef2f2; border: 1px solid #fecaca; padding: 20px; border-radius: 8px; margin: 20px 0;">
              <p style="font-size: 15px; color: #991b1b; margin: 0;">
                <strong>Important:</strong> Supabase has sent you a separate email with the actual password reset link. Please check your inbox (and spam folder) for an email from Supabase.
              </p>
            </div>
            <p style="font-size: 16px; color: #475569; line-height: 1.6;">Click the link in that email to create a new password. The link will expire in 24 hours.</p>
            <p style="font-size: 14px; color: #64748b; margin-top: 20px;">If you didn't request this password reset, you can safely ignore this email. Your password will remain unchanged.</p>
            <div style="text-align: center; margin: 30px 0;">
              <a href="${siteUrl}/auth/login" style="background: #64748b; color: white; padding: 12px 24px; text-decoration: none; border-radius: 8px; font-weight: bold; font-size: 14px; display: inline-block;">Back to Login</a>
            </div>
          </div>
          <p style="color: #94a3b8; font-size: 12px; text-align: center; margin-top: 20px;">- The TechVerse Hub Team</p>
        </div>
      `,
    };

    await transporter.sendMail(mailOptions);

    return NextResponse.json({ success: true });
  } catch (error: any) {
    console.error("Email error:", error);
    return NextResponse.json(
      { error: error.message || "Email failed to send" },
      { status: 500 }
    );
  }
}

