    solution: `#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <unistd.h>
#include <sys/wait.h>

void parseCommand(char* input, char** args) {
    int i = 0;
    args[i] = strtok(input, " ");
    while (args[i] != NULL) {
        args[++i] = strtok(NULL, " ");
    }
}

void executeCommand(char** args) {
    pid_t pid = fork();
    if (pid == 0) {
        execvp(args[0], args);
        perror("exec failed");
        exit(1);
    } else {
        wait(NULL);
    }
}

int main() {
    char input[256];
    char* args[10];

    while (1) {
        printf("myshell> ");
        if (!fgets(input, 256, stdin)) break;
        input[strlen(input)-1] = '\\0';

        if (strcmp(input, "exit") == 0) break;
        if (strlen(input) == 0) continue;

        parseCommand(input, args);
        executeCommand(args);
    }
    return 0;
}
