vector<int> twoSum(vector<int>& nums, int target) {
    unordered_map<int,int> mp;
    for(int i=0;i<nums.size();i++){
        int diff = target - nums[i];
        if(mp.count(diff)) return {mp[diff], i};
        mp[nums[i]] = i;
    }
    return {};
}

void reverseString(vector<char>& s) {
    int l=0, r=s.size()-1;
    while(l<r) swap(s[l++], s[r--]);
}

bool isAnagram(string s, string t) {
    if(s.size()!=t.size()) return false;
    array<int,26> cnt={0};
    for(int i=0;i<s.size();i++){
        cnt[s[i]-'a']++;
        cnt[t[i]-'a']--;
    }
    return all_of(cnt.begin(), cnt.end(), [](int x){return x==0;});
}

bool containsDuplicate(vector<int>& nums) {
    unordered_set<int> st;
    for(int n:nums){
        if(st.count(n)) return true;
        st.insert(n);
    }
    return false;
}

int maxSubArray(vector<int>& nums) {
    int best = nums[0], cur = nums[0];
    for(int i=1;i<nums.size();i++){
        cur = max(nums[i], cur+nums[i]);
        best = max(best, cur);
    }
    return best;
}

void moveZeroes(vector<int>& nums) {
    int pos=0;
    for(int n:nums) if(n!=0) nums[pos++] = n;
    while(pos<nums.size()) nums[pos++] = 0;
}

int singleNumber(vector<int>& nums) {
    int x=0; for(int n:nums) x^=n; return x;
}

int findMin(vector<int>& nums) {
    int l=0, r=nums.size()-1;
    while(l<r){
        int m=(l+r)/2;
        if(nums[m] > nums[r]) l=m+1;
        else r=m;
    }
    return nums[l];
}

bool isValid(string s) {
    stack<char> st;
    for(char c:s){
        if(c=='('||c=='['||c=='{') st.push(c);
        else{
            if(st.empty()) return false;
            char p=st.top(); st.pop();
            if((c==')'&&p!='(')||(c==']'&&p!='[')||(c=='}'&&p!='{')) return false;
        }
    }
    return st.empty();
}

int fib(int n) {
    if(n<2) return n;
    int a=0,b=1;
    for(int i=2;i<=n;i++){
        int t=a+b;
        a=b; b=t;
    }
    return b;
}
