solution:
#include <iostream>
#include <vector>
#include <stdexcept>
using namespace std;

template<typename T>
class Stack {
    vector<T> data;

public:
    void push(const T& item) {
        data.push_back(item);
    }

    T pop() {
        if (empty()) throw runtime_error("Stack empty");
        T item = data.back();
        data.pop_back();
        return item;
    }

    T& top() {
        if (empty()) throw runtime_error("Stack empty");
        return data.back();
    }

    bool empty() const { return data.empty(); }
    size_t size() const { return data.size(); }
};

int main() {
    Stack<int> intStack;
    intStack.push(10);
    intStack.push(20);
    intStack.push(30);

    while (!intStack.empty()) {
        cout << intStack.pop() << " ";
    }
    cout << endl;
    return 0;
}
