int[] arr = {1,2,2,3};
var seen = new HashSet<int>();
var res = arr.Where(x => seen.Add(x)).ToList();
Console.WriteLine(string.Join(",", res));

string s = "Level".ToLower();
string rev = new string(s.Reverse().ToArray());
Console.WriteLine(s == rev);

string s = "a b a";
var words = s.Split(' ');
var dict = new Dictionary<string,int>();
foreach (var w in words)
    dict[w] = dict.ContainsKey(w) ? dict[w] + 1 : 1;
Console.WriteLine(string.Join(",", dict.Select(k=>k.Key+":"+k.Value)));

var arr = new List<string>{"banana","apple"};
arr.Sort();
Console.WriteLine(string.Join(",", arr));

int[] arr = {1,2,3,4};
var evens = arr.Where(x => x % 2 == 0).ToList();
Console.WriteLine(string.Join(",", evens));

