solution:
:root {
  --main-color: #4caf50;
}
button {
  background-color: var(--main-color);
  color: white;
  padding: 10px;
}

.box {
  position: relative;
  animation: slide 3s linear infinite;
}
@keyframes slide {
  from { left: 0; }
  to { left: 150px; }
}

.content h2 + p {
  color: red;
  font-weight: bold;
}

.grid {
  display: grid;
  grid-template-areas:
    "header"
    "content"
    "footer";
  gap: 10px;
}
.header { grid-area: header; }
.content { grid-area: content; }
.footer { grid-area: footer; }

.card {
  transition: transform 0.4s;
  transform-style: preserve-3d;
}
.card:hover {
  transform: rotateY(20deg);
}

.gallery {
  display: grid;
  gap: 20px;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
}

.container {
  position: relative;
  width: 300px;
}
.container img {
  width: 100%;
}
.overlay {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0,0,0,0.6);
  opacity: 0;
  transition: opacity 0.3s;
}
.container:hover .overlay {
  opacity: 1;
}
