package main
import "fmt"
func main(){
    arr := []int{1,2,2,3}
    seen := map[int]bool{}
    res := []int{}
    for _, v := range arr {
        if !seen[v] {
            seen[v] = true
            res = append(res, v)
        }
    }
    fmt.Println(res)
}

package main
import "fmt"
import "strings"
func main(){
    s := strings.ToLower("Level")
    r := []rune(s)
    for i, j := 0, len(r)-1; i < j; i, j = i+1, j-1 {
        if r[i] != r[j] {
            fmt.Println(false)
            return
        }
    }
    fmt.Println(true)
}

