var addTwoNumbers = function(l1, l2) {
  let dummy = { val:0, next:null };
  let cur = dummy;
  let carry = 0;
  while (l1 || l2) {
    let x = l1 ? l1.val : 0;
    let y = l2 ? l2.val : 0;
    let sum = x + y + carry;
    carry = Math.floor(sum / 10);
    cur.next = { val: sum % 10, next: null };
    cur = cur.next;
    if (l1) l1 = l1.next;
    if (l2) l2 = l2.next;
  }
  if (carry) cur.next = { val: carry, next: null };
  return dummy.next;
};

var lengthOfLongestSubstring = function(s) {
  let map = new Map();
  let start = 0, maxLen = 0;
  for (let i = 0; i < s.length; i++) {
    if (map.has(s[i])) start = Math.max(start, map.get(s[i]) + 1);
    map.set(s[i], i);
    maxLen = Math.max(maxLen, i - start + 1);
  }
  return maxLen;
};

var search = function(nums, target) {
  let l = 0, r = nums.length - 1;
  while (l <= r) {
    let m = Math.floor((l + r) / 2);
    if (nums[m] === target) return m;
    if (nums[l] <= nums[m]) {
      if (nums[l] <= target && target < nums[m]) r = m - 1;
      else l = m + 1;
    } else {
      if (nums[m] < target && target <= nums[r]) l = m + 1;
      else r = m - 1;
    }
  }
  return -1;
};

var maxArea = function(height) {
  let l = 0, r = height.length - 1;
  let ans = 0;
  while (l < r) {
    let h = Math.min(height[l], height[r]);
    ans = Math.max(ans, h * (r - l));
    if (height[l] < height[r]) l++;
    else r--;
  }
  return ans;
};

var findKthLargest = function(nums, k) {
  nums.sort((a,b)=>b-a);
  return nums[k-1];
};

var topKFrequent = function(nums, k) {
  const freq = new Map();
  for (let n of nums) freq.set(n, (freq.get(n)||0)+1);
  const buckets = Array(nums.length+1).fill(null).map(()=>[]);
  for (let [n,c] of freq) buckets[c].push(n);
  const res = [];
  for (let i = buckets.length-1; i>=0 && res.length<k; i--) {
    for (let n of buckets[i]) {
      res.push(n);
      if (res.length===k) break;
    }
  }
  return res;
};

var productExceptSelf = function(nums) {
  let n = nums.length;
  let out = new Array(n);
  out[0] = 1;
  for (let i = 1; i < n; i++) out[i] = out[i-1] * nums[i-1];
  let suffix = 1;
  for (let i = n-1; i >= 0; i--) {
    out[i] *= suffix;
    suffix *= nums[i];
  }
  return out;
};

var numIslands = function(grid) {
  if (!grid.length) return 0;
  let m = grid.length, n = grid[0].length;
  let count = 0;
  function dfs(i,j) {
    if (i<0||j<0||i>=m||j>=n||grid[i][j]==='0') return;
    grid[i][j]='0';
    dfs(i+1,j); dfs(i-1,j); dfs(i,j+1); dfs(i,j-1);
  }
  for (let i=0;i<m;i++)
    for (let j=0;j<n;j++)
      if (grid[i][j]==='1') { count++; dfs(i,j); }
  return count;
};

var coinChange = function(coins, amount) {
  let dp = new Array(amount+1).fill(Infinity);
  dp[0] = 0;
  for (let c of coins) {
    for (let i=c; i<=amount; i++) dp[i] = Math.min(dp[i], dp[i-c]+1);
  }
  return dp[amount]===Infinity ? -1 : dp[amount];
};

var wordBreak = function(s, words) {
  let set = new Set(words);
  let dp = new Array(s.length+1).fill(false);
  dp[0] = true;
  for (let i=1; i<=s.length; i++) {
    for (let j=0; j<i; j++) {
      if (dp[j] && set.has(s.slice(j,i))) { dp[i] = true; break; }
    }
  }
  return dp[s.length];
};
