open class Animal(val name:String){ fun speak() = println("$name makes a sound") }
class Dog(name:String): Animal(name)
Dog("Buddy").speak()

interface Speaker { fun speak() = println("Hello") }
class Greeter: Speaker{}
Greeter().speak()

