#include <iostream>
#include <memory>
#include <string>

class Resource {
    std::string name;
public:
    Resource(const std::string& n) : name(n) {
        std::cout << name << " created" << std::endl;
    }
    ~Resource() {
        std::cout << name << " destroyed" << std::endl;
    }
    void use() { std::cout << "Using " << name << std::endl; }
};

int main() {
    // unique_ptr - exclusive ownership
    {
        auto ptr = std::make_unique<Resource>("Unique");
        ptr->use();
    }  // Automatically destroyed here

    // shared_ptr - shared ownership
    std::shared_ptr<Resource> shared1;
    {
        auto shared2 = std::make_shared<Resource>("Shared");
        shared1 = shared2;  // Reference count = 2
        std::cout << "Count: " << shared2.use_count() << std::endl;
    }  // shared2 gone, count = 1
    shared1->use();
    // Destroyed when shared1 goes out of scope

    // weak_ptr - non-owning reference
    std::weak_ptr<Resource> weak;
    {
        auto shared = std::make_shared<Resource>("Weak Test");
        weak = shared;
        if (auto locked = weak.lock()) {
            locked->use();
        }
    }
    // weak.lock() now returns nullptr

    return 0;
}

