codeExample:
def counter(n):
    for i in range(n):
        yield i

for x in counter(3):
    print(x)

# simple decorator
from functools import wraps

def debug(f):
    @wraps(f)
    def wrapper(*a,**k):
        print('call', f.__name__)
        return f(*a,**k)
    return wrapper

@debug
def add(a,b):
    return a+b

print(add(2,3))

