import json
import os

todos = []
FILENAME = "todos.json"

def load_todos():
    global todos
    if os.path.exists(FILENAME):
        with open(FILENAME, 'r') as f:
            todos = json.load(f)

def save_todos():
    with open(FILENAME, 'w') as f:
        json.dump(todos, f, indent=2)

def get_next_id():
    return max([t['id'] for t in todos], default=0) + 1

def add_task(description):
    todo = {'id': get_next_id(), 'task': description, 'completed'}
    todos.append(todo)
    save_todos()
    print(f"Added task #{todo['id']}")

def view_tasks():
    if not todos:
        print("No tasks!")
        return
    for t in todos:
        status = "Done" if t['completed'] else "Pending"
        print(f"[{t['id']}] {t['task']} - {status}")

def complete_task(task_id):
    for t in todos:
        if t['id'] == task_id:
            t['completed'] = True
            save_todos()
            print(f"Completed task #{task_id}")
            return
    print("Task not found")

def delete_task(task_id):
    global todos
    todos = [t for t in todos if t['id'] != task_id]
    save_todos()
    print(f"Deleted task #{task_id}")

def main():
    load_todos()
    while True:
        print("\n=== Todo CLI ===")
        print("1. Add  2. View  3. Complete  4. Delete  5. Exit")
        c = input("Choose: ")
        if c == "1": add_task(input("Task: "))
        elif c == "2": view_tasks()
        elif c == "3": complete_task(int(input("ID: ")))
        elif c == "4": delete_task(int(input("ID: ")))
        elif c == "5": break

if __name__ == "__main__":
    main()
